import React, { Component } from 'react';
import {Element} from 'react-scroll';
import './about.css';
//import Image from './me.png';
import {Link} from 'react-scroll';
import pdf from './cv.pdf';

class About extends Component {

  render() {
    return (
    	<div id='about'>
      <h1 style={{position:'relative',top:100}} className='head1'>ABOUT</h1>
      <h3 style={{fontFamily:'B612 Mono',position:'relative',top:55}}  className='head2'>MY BIODATA</h3>
      <div style={{position:'absolute',top:270}}>

      <div style={{position:'absolute',top:-50}}>
        <div className='border2'>
            <div className='border1'>
    		  <img src={Image}/>
            </div>
        </div>
    		<div style={{fontFamily:'Amiri',position:'absolute',top:60,left:630,width:600,height:430}}>
          <br/>

          <h5>Hi, I am Anjali Jain.I hail from the beautiful town of Udaipur in Rajasthan, India.
          I am an undergraduate currently in my prefinal year, pursuing
          B.Tech + M.Tech degree in Information Technology.
          </h5>
          <br/>
          <h5>I have deep interest in computer programming and software development and am always curious to 
          learn.
          I keep exploring all the new technologies and love to stay updated about them 
          and believe that right innovation and integrations can work
          wonders.
          </h5>

          <div style={{position:'relative',left:0,top:50}}>
    		  <Link
              activeClass="active"
              to="skills"
              spy={true}
              smooth={true}
              offset={0}
              duration= {500}><button className='skillbtn'>SKILLS</button>
            </Link>
          <span style={{position:'relative',top:7}}>
            <a style={{textDecoration:'none'}} href={pdf} download="Anjali Jain_CV.pdf">
            <button className='downloadbtn'>
            <span style={{display:'flex',justifyContent:'space-between'}}>
              <span className="material-icons">file_download</span><span> DOWNLOAD CV</span>
            </span>
            </button></a>
        </span>

        </div>
        
        </div>


    		</div>
        </div>
    	</div >
    );
  }
}

export default About;
