import React, { Component } from 'react';
import './skills.css';
import Aos from 'aos';
import 'aos/dist/aos.css';
import Img from './skill.jpg';
import butter from './butter-client';

Aos.init({
 duration: 1200
});

class Skills extends Component {

  constructor(props){
    super(props);
    this.state={
        skills:Array
    }
  }

  componentWillMount(){
     let curr=this;
     butter.post.list({page: 1, page_size: 10}).then(function(response) {
        curr.setState({skills:response.data.data[0].summary});
        console.log(response.data.data[0])
    })
  }

  render() {
    return (
    	<div id='skills'>
        <div style={{position:'relative',top:70}}>
            <div className='parallax' 
            style={{backgroundImage:'url(' + Img + ')',height:'auto',maxWidth:'100%'}}>
            <div className='ovrlay'></div>
            </div>
            <h1 className='head1'  style={{position:'absolute',top:35,left:555}}>ABOUT</h1>
            <h3 className='head2' style={{position:'absolute',top:70,left:560,color:'white'}}>MY SKILLS</h3>

            <h4 style={{position:'absolute',top:173,left:110,color:'white'}}>HTML</h4>
    		<div className="myProgress" style={{top:200,left:100}}>
                <div className="myBar" data-aos="new-animation1">{this.state.skill1}</div>
            </div>
            <h4 style={{position:'absolute',top:280,left:110,color:'white'}}>HTML</h4>
            <div className="myProgress" style={{top:300,left:100}}>
                <div className="myBar" data-aos="new-animation2">70%</div>
            </div>
            <h4 style={{position:'absolute',top:370,left:110,color:'white'}}>HTML</h4>
            <div className="myProgress" style={{top:400,left:100}}>
                <div className="myBar" data-aos="new-animation3">90%</div>
            </div>
            <div className="myProgress" style={{top:210,left:700}}>
                <div className="myBar" data-aos="new-animation4">30%</div>
            </div>
            <div className="myProgress" style={{top:310,left:700}}>
                <div className="myBar" data-aos="new-animation5">60%</div>
            </div>
            <div className="myProgress" style={{top:410,left:700}}>
                <div className="myBar" data-aos="new-animation6">80%</div>
            </div>
        </div>
    	</div>
    );
  }
}

export default Skills;
