import React, { Component } from 'react';
import Isotope from 'isotope-layout';
import './portfolio.css';
import Img from './person.jpg';
import Img2 from'./f.jpg';
import Img3 from'./img1.jpg';

const Portfolio = () => {
  const [isotope, setIsotope] = React.useState(null);

  const [filterKey, setFilterKey] = React.useState("*");

  React.useEffect(() => {
    setIsotope(
      new Isotope(".filter-container", {
        itemSelector: ".filter-item",
        layoutMode: "fitRows"
      })
    );
  }, []);

  React.useEffect(
    () => {
      if (isotope) {
        filterKey === "*"
          ? isotope.arrange({ filter: `*` })
          : isotope.arrange({ filter: `.${filterKey}` });
      }
    },
    [isotope, filterKey]
  );

  return (
    <div id='portfolio'>
     <div style={{position:'relative',top:100}}>
      <h1> Portfolio </h1>
      <br/>
      <br/>
      <button className='btn' onClick={() => setFilterKey("*")}><h4>All</h4></button>
      <button className='btn' onClick={() => setFilterKey("vege")}><h4>Node.js</h4></button>
      <button className='btn' onClick={() => setFilterKey("fruit")}><h4>React.js</h4></button>
      <br/><br/>
      <div className='box'>
      <ul className="filter-container">
        <div className="filter-item vege">
          <span className='con1'>
            <div className='pro'></div>
            <div class="overlay">
                <div class="text">Hello World</div>
            </div>
          </span>
        </div>
        <div className="filter-item fruit">
          <span className='con2'><img src={Img2} className='project' />
            <div className="overlay">
                <div className="text">Hello World</div>
            </div>
          </span>
        </div>
        <div className="filter-item fruit">
          <span><img src={Img2} className='project' /></span>
        </div>
        <div className="filter-item fruit">
          <span><img src={Img3} className='project' /></span>
        </div>
        <div className="filter-item fruit vege">
          <span><img src={Img} className='project' /></span>
        </div>
        <div className="filter-item vege">
          <span><img src={Img3} className='project' /></span>
        </div>
      </ul>
      </div>
      </div>
    </div>
  );
};

export default Portfolio;
