import React, { Component } from 'react';
import Isotope from 'isotope-layout';
import Img from './person.jpg';

const Portfolio = () => {
  const [isotope, setIsotope] = React.useState(null);

  const [filterKey, setFilterKey] = React.useState("*");

  React.useEffect(() => {
    setIsotope(
      new Isotope(".filter-container", {
        itemSelector: ".filter-item",
        layoutMode: "fitRows"
      })
    );
  }, []);

  React.useEffect(
    () => {
      if (isotope) {
        filterKey === "*"
          ? isotope.arrange({ filter: `*` })
          : isotope.arrange({ filter: `.${filterKey}` });
      }
    },
    [isotope, filterKey]
  );

  return (
    <div id='portfolio'>
      <h1> Portfolio </h1>
      <button onClick={() => setFilterKey("*")}>All</button>
      <button onClick={() => setFilterKey("vege")}>Node.js</button>
      <button onClick={() => setFilterKey("fruit")}>React.js</button>
      <hr />
      <ul className="filter-container">
        <div className="filter-item vege">
          <span><img src={Img} className='project'/></span>
        </div>
        <div className="filter-item fruit">
          <span><img src={Img} className='project' /></span>
        </div>
        <div className="filter-item fruit">
          <span><img src={Img} className='project' /></span>
        </div>
        <div className="filter-item fruit">
          <span><img src={Img} className='project' /></span>
        </div>
        <div className="filter-item fruit vege">
          <span><img src={Img} className='project' /></span>
        </div>
        <div className="filter-item vege">
          <span><img src={Img} className='project' /></span>
        </div>
      </ul>
    </div>
  );
};

export default Portfolio;
