import React, { Component } from 'react';
import Img from './person.jpg';
import filterizr,{$} from 'filterizr';
 import Filterizr from './filter';

class User{
  constructor() {
     this.menu = $("#navItems");
    this.filterizr = $(".filtr-container__row");
    this.filterizrMenu();
    this.filterizrSetup();
  }
    filterizrMenu() {
      this.menu.on('click', 'li', function() {
        $(this).addClass("filtr-active").siblings().removeClass("filtr-active");
      });
    }
    filterizrSetup() {
      this.filterizr.on('load', function() {
          var filterizd = $(this).filterizr();
      });
    }
}

//const users=new User();

const options = {
  animationDuration: 0.5, // in seconds
  callbacks: { 
    onFilteringStart: function() { },
    onFilteringEnd: function() { },
    onShufflingStart: function() { },
    onShufflingEnd: function() { },
    onSortingStart: function() { },
    onSortingEnd: function() { }
  },
  controlsSelector: '', // Selector for custom controls
  delay: 0, // Transition delay in ms
  delayMode: 'progressive', // 'progressive' or 'alternate'
  easing: 'ease-out',
  filter: 'all', // Initial filter
  filterOutCss: { // Filtering out animation
    opacity: 0,
    transform: 'scale(0.5)'
  },
  filterInCss: { // Filtering in animation
    opacity: 0,
    transform: 'scale(1)'
  },
  gridItemsSelector: '.filtr-container',
  gutterPixels: 0, // Items spacing in pixels
  layout: 'sameSize', // See layouts
  multifilterLogicalOperator: 'or',
  searchTerm: '',
  setupControls: true, // Should be false if controlsSelector is set 
  spinner: { // Configuration for built-in spinner
    enabled: false,
    fillColor: '#2184D0',
    styles: {
      height: '75px',
      margin: '0 auto',
      width: '75px',
      'z-index': 2,
    },
  },
} 

const filter = new User('.filter-container',options);

class Portfolio extends Component {

  render() {
    return (

      <div class="filter-container">
  // <div class="filtr-item" data-category="1" data-sort="value">
  //   <img src={Img}/>
  // </div>
  // <div class="filtr-item" data-category="2, 1" data-sort="value">
  //   <img src={Img} />
  // </div>
  // <div class="filtr-item" data-category="1, 3" data-sort="value">
  //   <img src={Img}/>
  // </div>
</div>
    );
  }
}

export default Portfolio;
