import $ from 'filterizr'; // Imports jQuery object extended with .filterizr function

class Filterizr {
  constructor() {
    this.menu = $("#navItems");
    this.filterizr = $(".filtr-container__row");
    this.filterizrMenu();
    this.filterizrSetup();
  }
    filterizrMenu() {
      this.menu.on('click', 'li', function() {
        $(this).addClass("filtr-active").siblings().removeClass("filtr-active");
      });
    }
    filterizrSetup() {
      this.filterizr.on('load', function() {
          var filterizd = $(this).filterizr();
      });
    }
  }

export default Filterizr;
